 VALUES (N'20051014171459',N'Delivery Plan by Hour Report',N'',N'','','SQL',N'SELECT            
MST_CCN.Code AS ''CCN'', 
SO_DeliverySchedule.ScheduleDate,
MST_Party.Code AS ''Customer'', 
MST_Party.Name,
ITM_Product.Code AS ''Part No'', 
ITM_Product.Description AS ''Part Name'', 
ITM_Product.Revision, 
SO_SaleOrderMaster.Code AS ''SO No'', 
SO_SaleOrderDetail.SaleOrderLine,
SO_SaleType.Code AS ''SaleType'', 
MST_UnitOfMeasure.Code AS ''SellingUM'',
SO_DeliverySchedule.DeliveryQuantity, 
RTG.ShipDate,
RTG.CommitQuantity  ''ShipQuantity'',
RTG.PostDate,
RTG.ReceiveQuantity 
FROM 
SO_SaleOrderMaster JOIN SO_SaleOrderDetail
    ON SO_SaleOrderMaster.SaleOrderMasterID = SO_SaleOrderDetail.SaleOrderMasterID
JOIN SO_DeliverySchedule
    ON SO_SaleOrderDetail.SaleOrderDetailID = SO_DeliverySchedule.SaleOrderDetailID
LEFT JOIN (SELECT SO_CommitInventoryDetail.ShipDate, SO_CommitInventoryDetail.CommitQuantity, SO_CommitInventoryDetail.DeliveryScheduleID,
SO_ReturnedGoodsMaster.TransDate AS PostDate, SO_ReturnedGoodsDetail.ReceiveQuantity
FROM SO_CommitInventoryDetail JOIN SO_DeliverySchedule
ON SO_CommitInventoryDetail.DeliveryScheduleID = SO_DeliverySchedule.DeliveryScheduleID
JOIN SO_ReturnedGoodsDetail
ON SO_DeliverySchedule.SaleOrderDetailID = SO_ReturnedGoodsDetail.SaleOrderDetailID
JOIN SO_ReturnedGoodsMaster
ON SO_ReturnedGoodsDetail.ReturnedGoodsMasterID = SO_ReturnedGoodsMaster.ReturnedGoodsMasterID) AS RTG
ON SO_DeliverySchedule.DeliveryScheduleID = RTG.DeliveryScheduleID
JOIN MST_CCN 
    ON SO_SaleOrderMaster.CCNID = MST_CCN.CCNID
JOIN MST_Party 
    ON SO_SaleOrderMaster.PartyID = MST_Party.PartyID
JOIN ITM_Product 
    ON ITM_Product.ProductID = SO_SaleOrderDetail.ProductID
LEFT JOIN SO_SaleType 
    ON SO_SaleOrderMaster.SaleTypeID = SO_SaleType.SaleTypeID
LEFT JOIN MST_UnitOfMeasure 
    ON SO_SaleOrderDetail.SellingUMID = MST_UnitOfMeasure.UnitOfMeasureID 
 WHERE  
        SO_DeliverySchedule.ScheduleDate >=@FromOrderDate
AND SO_DeliverySchedule.ScheduleDate <=@ToOrderDate
AND SO_SaleOrderMaster.SaleTypeID = @SaleTypeID 
AND  MST_Party.Code = @CustomerCode 
AND ITM_Product.CategoryID = @CategoryID 
AND  ITM_Product.Code = @PartNumber   
AND ITM_Product.Description = @PartName 
AND ITM_Product.Revision = @Model 
AND SO_SaleOrderMaster.Code = @SONumber
AND MST_CCN.CCNID = @CCNID',1440,1440,1440,1440,0,0,1,8,0,N'',N'Arial Narrow|24|Bold|0|False|Point',N'Arial Narrow|8|Regular|1|False|Point',N'Microsoft Sans Serif|8.25|Bold|0|False|Point',N'Arial Narrow|8|Bold|1|False|Point',N'Arial Narrow|8.25|Regular|0|False|Point',N'Arial Narrow|8|Regular|1|False|Point',N'Arial Narrow|8|Regular|1|False|Point',1,N'DeliveryPlanByHourReport.xml')

