 VALUES (N'20051122091908730',N'Production Line and Make Item Reference List',N'',N'','','SQL',N'select 
0 as [No.],
ITM_Product.Code as [Part No.],
ITM_Product.Description as [Part Name],
ITM_Product.Revision as [Model],
ITM_Routing.Step as [RoutingStep],
case
when ITM_Routing.Type = 0 then ''Inside''
when ITM_Routing.Type = 1 then ''Outside''
end 
as [Type],
ITM_Routing.MachineRunTime as [Machine Run],
ITM_Routing.Machines as [No. of Machine],
ITM_Routing.LaborRunTime as [Labor Run],
ITM_Routing.CrewSize as [Screw Size],
ITM_Routing.Pacer as [Pacer],
ITM_Routing.OverlapQty as [Overlap Qty],
ITM_Routing.OverlapPercent as [Overlap %],
ITM_Routing.ScheduleSeq as [Schedule Seq],

[Fixed LT] = 
CASE
WHEN ITM_Routing.Type = 1 THEN ITM_Routing.FixLT
WHEN ITM_Routing.Type = 0 AND ITM_Routing.Pacer = ''L'' THEN ITM_Routing.LaborRunTime 
WHEN ITM_Routing.Type = 0 AND ITM_Routing.Pacer = ''M'' THEN ITM_Routing.MachineRunTime 
WHEN ITM_Routing.Type = 0 AND ITM_Routing.Pacer = ''B'' THEN ITM_Routing.LaborRunTime + ITM_Routing.MachineRunTime 
END,
ITM_Routing.OSCost as [Cost],MST_Party.Code as [Vendor]
from 
MST_WorkCenter
join ITM_Routing
on MST_WorkCenter.WorkCenterID = ITM_Routing.WorkCenterID
join ITM_Product
on ITM_Routing.ProductID = ITM_Product.ProductID
left join MST_Party
on ITM_Routing.PartyID = MST_Party.PartyID
left join PRO_ProductionLine
on MST_WOrkCenter.ProductionLineID = PRO_ProductionLine.ProductionLineID 
where MST_WorkCenter.CCNID = @CCNID
and PRO_ProductionLine.ProductionLineID = @ProductionLineID
and MST_WorkCenter.WorkCenterID = @WorkCenterID
Order by [Part No.], [Part Name], [Model], [RoutingStep]',432,432,432,432,0,0,1,9,0,N'',N'Arial Narrow|12|Bold|1|False|Point',N'Arial Narrow|9|Bold|1|False|Point',N'Arial Narrow|8.25|Bold|0|False|Point',N'Arial Narrow|9|Bold|1|False|Point',N'Microsoft Sans Serif|8.25|Regular|0|False|Point',N'Arial Narrow|8|Regular|1|False|Point',N'Arial Narrow|9|Regular|1|False|Point',0,N'')

