 VALUES (N'20060124141252233',N'Warning Min-Max-Stock Report',N'',N'','','SQL',N'SELECT	0 AS ''NO'', ITM_Category.Code AS ''Category'', ITM_Source.Code AS ''Source'', MST_Party.Code AS ''Vendor'',
	ITM_Product.Code AS ''PartNo'', ITM_Product.Description AS ''PartName'', ITM_Product.Revision AS ''Model'',
	MST_UnitOfMeasure.Code AS ''StockUM'', 
	(SELECT ISNULL(SUM(ISNULL(OHQuantity,0)),0) AS OKQuantity FROM IV_BinCache JOIN MST_BIN
	ON IV_BinCache.BinID = MST_BIN.BinID
	JOIN enm_BinType ON MST_BIN.BinTypeID = enm_BinType.BinTypeID
	WHERE IV_BinCache.MasterLocationID = @MasterLocationID AND enm_BinType.Name IN (''OK'', ''BF'')
	AND IV_BinCache.ProductID = ITM_Product.ProductID) AS OK,
	(SELECT ISNULL(SUM(ISNULL(OHQuantity,0)),0) AS OKQuantity FROM IV_BinCache JOIN MST_BIN
	ON IV_BinCache.BinID = MST_BIN.BinID
	JOIN enm_BinType ON MST_BIN.BinTypeID = enm_BinType.BinTypeID
	WHERE IV_BinCache.MasterLocationID = @MasterLocationID AND enm_BinType.Name = ''NG''
	AND IV_BinCache.ProductID = ITM_Product.ProductID) AS NG,
	(SELECT ISNULL(SUM(ISNULL(OHQuantity,0)),0) AS OKQuantity FROM IV_BinCache JOIN MST_BIN
	ON IV_BinCache.BinID = MST_BIN.BinID
	JOIN enm_BinType ON MST_BIN.BinTypeID = enm_BinType.BinTypeID
	WHERE IV_BinCache.MasterLocationID = @MasterLocationID AND enm_BinType.Name = ''DS''
	AND IV_BinCache.ProductID = ITM_Product.ProductID) AS DS,
	ISNULL(ITM_Product.SafetyStock, 0) AS SafetyStock, ISNULL(ITM_Product.MinimumStock, 0) AS ''MinStock'', 
	ISNULL(ITM_Product.MaximumStock, 0) AS ''MaxStock''
FROM ITM_Product LEFT JOIN ITM_Category
ON ITM_Product.CategoryID = ITM_Category.CategoryID
LEFT JOIN ITM_Source
ON ITM_Product.SourceID = ITM_Source.SourceID
LEFT JOIN MST_Party
ON ITM_Product.PrimaryVendorID = MST_Party.PartyID
JOIN MST_UnitOfMeasure
ON ITM_Product.StockUMID = MST_UnitOfMeasure.UnitOfMeasureID
WHERE ITM_Product.CCNID = @CCNID
AND ITM_Product.CategoryID IN (@CategoryID)
AND ITM_Product.SourceID = @SourceID
AND ITM_Product.Revision IN (@Model)
AND ITM_Product.ProductID IN (@ProductID)',720,720,720,720,0,0,0,9,0,N'',N'Arial Narrow|12|Bold|1|False|Point',N'Arial Narrow|6|Bold|1|False|Point',N'Arial Narrow|6.5|Bold|1|False|Point',N'Arial Narrow|6|Bold|1|False|Point',N'Arial Narrow|6|Regular|1|False|Point',N'Arial Narrow|6|Regular|1|False|Point',N'Arial Narrow|6|Regular|1|False|Point',1,N'WarningMaxMinStock.xml')

