 VALUES (N'20060315100920780',N'Shipping Management Transaction History',N'',N'','','SQL',N'SELECT DISTINCT 0 AS No, SO_ConfirmShipMaster.ShippedDate, SO_ConfirmShipMaster.ConfirmShipNo,
