 VALUES (N'20060605100737827',N'Attachment of Importing-Invoice',N'',N'','','SQL',N'SELECT  DISTINCT 	
	product.Revision PartModel,
	cate.Name CategoryCode,
	refDetail.CustomerItemCode PartNo,
	product.Description PartName,	
	product.QuantitySet,	
	(SELECT SUM(detail.InvoiceQty)
	 FROM SO_ConfirmShipDetail detail
	 WHERE detail.SaleOrderDetailID = soDetail.SaleOrderDetailID
	       AND detail.ConfirmShipMasterID = shipMaster.ConfirmShipMasterID
	       AND detail.ConfirmShipDetailID = shipdetail.ConfirmShipDetailID 	
	) ShippingQuantity,		
	(
	 (SELECT SUM( detail.Price * detail.InvoiceQty)
	  FROM SO_ConfirmShipDetail detail
	  WHERE detail.ConfirmShipMasterID = shipMaster.ConfirmShipMasterID
	  AND detail.ProductID = product.ProductID
	  )	
	  /
	 (
	 Case
	  When(
	     SELECT SUM(detail.InvoiceQty)
	     FROM SO_ConfirmShipDetail detail
	     WHERE detail.ConfirmShipMasterID = shipMaster.ConfirmShipMasterID
	     AND detail.ProductID = product.ProductID
	   ) = 0 
	   then 1
	   else   
  	     (
	      SELECT SUM(detail.InvoiceQty)
	      FROM SO_ConfirmShipDetail detail
	      WHERE detail.ConfirmShipMasterID = shipMaster.ConfirmShipMasterID
	      AND detail.ProductID = product.ProductID
	      )
	    end      
	 )
	) UnitPrice,
	(SELECT SUM(detail.InvoiceQty)
	 FROM SO_ConfirmShipDetail detail
	 WHERE detail.SaleOrderDetailID = soDetail.SaleOrderDetailID
	       AND detail.ConfirmShipMasterID = shipMaster.ConfirmShipMasterID
	) * shipdetail.Price NetAmount,
	shipMaster.InvoiceNo ConfirmShipNo,
	shipMaster.ReferenceNo CustomerPurchaseOrderNo
	
FROM    SO_ConfirmShipDetail shipdetail
	JOIN SO_ConfirmShipMaster shipMaster ON shipdetail.ConfirmShipMasterID = shipMaster.ConfirmShipMasterID 
	JOIN SO_SaleOrderMaster soMaster ON shipMaster.SaleOrderMasterID = soMaster.SaleOrderMasterID 
	JOIN SO_SaleOrderDetail soDetail ON shipdetail.SaleOrderDetailID = soDetail.SaleOrderDetailID 
	JOIN ITM_Product product ON product.ProductID = soDetail.ProductID
	JOIN MST_Party party ON soMaster.PartyID = party.PartyID
	LEFT JOIN ITM_Category cate ON cate.CategoryID = product.CategoryID
	LEFT JOIN SO_CustomerItemRefMaster refMaster ON refMaster.PartyID = party.PartyID
	LEFT JOIN SO_CustomerItemRefDetail refDetail ON refMaster.CustomerItemRefMasterID = refDetail.CustomerItemRefMasterID
		AND refDetail.ProductID = product.ProductID

WHERE shipMaster.ConfirmShipMasterID = @ConfirmShipMasterID
ORDER BY product.Revision,
	product.Description',720,720,720,720,0,0,0,9,0,N'',N'Arial Narrow|12|Bold|1|False|Point',N'Arial Narrow|6|Bold|1|False|Point',N'Arial Narrow|6.5|Bold|1|False|Point',N'Arial Narrow|6|Bold|1|False|Point',N'Arial Narrow|6|Regular|1|False|Point',N'Arial Narrow|6|Regular|1|False|Point',N'Arial Narrow|6|Regular|1|False|Point',1,N'SOInvoice_Attachment.xml')

