 VALUES (N'20060704171811640',N'Unallocated Items Report',N'',N'','','SQL',N'SELECT dept.Code DeptCode,pline.Code ProLineCode,pgroup.Code GroupCode,element.Name
CostElement,alloc_detail.AllocationAmount FROM cst_ActCostAllocationMaster alloc INNER JOIN cst_ActCostAllocationDetail
alloc_detail ON alloc.ActCostAllocationMasterID=alloc_detail.ActCostAllocationMasterID INNER JOIN STD_CostElement element ON 
element.CostElementID=alloc_detail.CostElementID LEFT JOIN MST_Department dept ON alloc_detail.DepartmentID = 
dept.DepartmentID LEFT JOIN PRO_ProductionLine pline ON alloc_detail.ProductionLineID = pline.ProductionLineID LEFT JOIN 
