result.ProductGroupID=alloc_detail.ProductGroupID AND result.ProductID=alloc_detail.ProductID)) OR (EXISTS(SELECT result.* 
FROM cst_AllocationResult result WHERE result.ActCostAllocationMasterID=alloc.ActCostAllocationMasterID AND 
result.CostElementID=alloc_detail.CostElementID AND result.DepartmentID=alloc_detail.DepartmentID AND 
result.ProductionLineID=alloc_detail.ProductionLineID AND result.ProductGroupID=alloc_detail.ProductGroupID)) OR 
(EXISTS(SELECT result.* FROM cst_AllocationResult result WHERE 
result.ActCostAllocationMasterID=alloc.ActCostAllocationMasterID AND result.CostElementID = alloc_detail.CostElementID AND 
result.DepartmentID =  alloc_detail.DepartmentID AND result.ProductionLineID = alloc_detail.ProductionLineID AND 
alloc_detail.ProductGroupID IS NULL AND result.ProductGroupID IS NULL)) OR (EXISTS(SELECT result.* FROM cst_AllocationResult result WHERE 
result.ActCostAllocationMasterID=alloc.ActCostAllocationMasterID AND result.CostElementID = alloc_detail.CostElementID AND 
result.DepartmentID=alloc_detail.DepartmentID AND alloc_detail.ProductionLineID IS NULL AND alloc_detail.ProductGroupID IS 
NULL AND result.ProductionLineID IS NULL AND result.ProductGroupID IS 
NULL)) OR (EXISTS(SELECT result.* FROM cst_AllocationResult result WHERE 
