 VALUES (N'20060813185324967',N'Actual Cost For Make Item',N'',N'','','SQL',N'/*
Cost Element Type: 1 - Machine, 2 - Labor, 3 - Material, 4 - SubMaterial, 5 - Overhead
*/
SELECT P.ProductID, P.Code ''Part Number'', P.Description ''Part Name'', P.Revision ''Model'', E.Name ''Element'', Z.AVEG, ITM_Category.Code AS Category
FROM (SELECT C.ProductID, C.CostElementID,C.ActualCost, C.WOCompletionQty,
C.ActualCost+D.DefMaterial ''AVEG'', C.ActCostAllocationMasterID
From CST_ActualCostHistory C
INNER JOIN 
(SELECT ProductID,(Sum(ComponentValue*WOCompletionQty)/Quantity) ''DefMaterial'',
ActCostAllocationMasterID
FROM CST_ActualCostHistory CA
JOIN STD_CostElement S ON CA.CostElementID = S.CostElementID
JOIN STD_CostElementType ET ON S.CostElementTypeID = ET.CostElementTypeID
WHERE ActCostAllocationMasterID= @period AND ET.Code <> 3 AND Quantity<>0
GROUP BY ProductID,Quantity,ActCostAllocationMasterID) D 
ON C.ProductID = D.ProductID
