 VALUES (N'20060916165135890',N'Tong Hop Vat Tu Nhap theo KH-Local',N'',N'','','SQL',N'select Years,Months,A.PartyID,PT.Code PartyCode,PT.Name VendorName,A.ProductID,
I.Code PartNo,I.Description,I.Revision,PTY.Code Type,CAT.Code Category,UM.Code UM,I.VAT Rvat,
sum(ReceiveQuantity) ReceiptQty,Sum(RCVAmount)RCVAmount,sum(VATAmount) VATAmount,
