Select DatePart(Year,Rm.PostDate) years,DatePart(month,Rm.PostDate) months,RM.PartyID,
RD.ProductID,0 ReceiveQuantity,0 RCVAmount,0 VATAmount, rd.Quantity RTQty,0 RTAmount, 0 RTVATAmount 
