where RM.ByPO=1
UNION ALL
Select DatePart(Year,fm.PostDate)Years,DatePart(month,fm.PostDate)months, 
fm.VendorID PartyID,fd.ProductID,0 ReceiveQuantity,0 RCVAmount,0 VATAmount,0 RTQty,FD.Amount*fm.ExchangeRate RTAmount,
