 VALUES (N'20061117152107810',N'Work Order Complement',N'',N'','','SQL',N'Select DatePart(Year,W.PostDate) Year,DatePart(Month,W.PostDate) Month,
DEP.Code Department,LOC.Code Location,BIN.Code Bin,''Work Order'' as Purpose,
