INNER JOIN MST_Location LOC ON LOC.LocationID=W.LocationID
LEFT JOIN MST_Department DEP ON DEP.DepartmentID=LOC.DepartmentID
INNER JOIN MST_Bin BIN ON BIN.BinID=W.BinID
INNER JOIN ITM_Product I ON I.ProductID=W.ProductID
INNER JOIN MST_UnitOfMeasure UM ON UM.UnitOfMeasureID=I.StockUMID
LEFT JOIN ITM_Category CAT ON CAT.CategoryID=I.CategoryID
