AND DatePart(Month,W.PostDate)=@month
AND W.LocationID in (@location) 
and W.BinID in (@bin) 
and CAT.CategoryID in (@cat)
and W.ProductID IN (@product)
Group by DatePart(Year,W.PostDate),DatePart(Month,W.PostDate),LOC.DepartmentID,W.LocationID,W.BinID,W.ProductID,
LOC.Code,DEP.Code,BIN.Code,I.Code,I.Revision,I.Description,UM.Code,CAT.Code,CAT.CategoryID,LOC.DepartmentID
Union all
Select DatePart(Year,M.PostDate) as Year,DatePart(Month,M.PostDate) as Month,
