(Select UnitCost from v_UnitOfActualCost WHERE ProductID=D.ProductID
AND DatePart(Year,FromDate)=@myYear
AND DatePart(Month,FromDate)=@myMonth)
Else
ISNULL((SELECT SUM(Cost)  	    
	FROM CST_STDItemCost   	    
	WHERE ProductID = D.ProductID),0)	End),0) Price,
Sum(ISNULL(D.CommitQuantity,0)) * 
ISNULL((Case
When
(Select UnitCost from v_UnitOfActualCost WHERE ProductID=D.ProductID
AND DatePart(Year,FromDate)= @myYear
AND DatePart(Month,FromDate)= @myMonth) 
Is NOT NULL Then
(Select UnitCost from v_UnitOfActualCost WHERE ProductID=D.ProductID
AND DatePart(Year,FromDate)=@myYear
AND DatePart(Month,FromDate)=@myMonth)
Else
ISNULL((SELECT SUM(Cost)  	    
	FROM CST_STDItemCost   	    
	WHERE ProductID = D.ProductID),0)	End),0) Amount
