JOIN ITM_PRODUCT I2 ON I2.ProductID=D.ProductID
JOIN MST_Location LOC ON LOC.LocationID=D.LocationID
JOIN MST_Bin BIN ON BIN.BinID=D.BinID
LEFT JOIN ITM_Category CAT1 ON I2.CategoryID=CAT1.CategoryID
LEFT JOIN ITM_Category CAT2 ON I1.CategoryID=CAT2.CategoryID
JOIN MST_UnitOfMeasure UM ON UM.UnitOfMeasureID=I2.StockUMID
WHERE  Datepart(year,M.PostDate)=@myYear
AND Datepart(Month,M.PostDate)=@myMonth
